import React, { useState } from 'react'
import './App.css'
import Axios from 'axios'

function Show({data}) {
  const [update,setUpdate]=useState('');
//DELETE Operation:
const deletefn=(id)=>{
Axios.delete(`http://localhost:3001/delete/${id}`)
}

//UPDATE Operation:
const updatefn=(id)=>{
Axios.put('http://localhost:3001/update',{task:update,id:id});
}


  return (
    <div>{data.map((item)=>{
        return(<ol>
        <input type='checkbox'/><h4 className='task'>{(item.task)}</h4><button onClick={()=>deletefn(item._id)}>DELETE</button>
        <input type='text' placeholder='Input the change...' onChange={(e)=>setUpdate(e.target.value)}/><button onClick={()=>updatefn(item._id)}>UPDATE</button>
        </ol>)
    })}</div>
  )
}

export default Show
