import React, { useEffect, useState } from 'react';
import './App.css';
import Axios from 'axios';
import Show from './Show';
function App() {
  const [data,setData]=useState([]);

  //Read Operation
  useEffect(()=>{
    Axios.get('http://localhost:3001/read').then((response)=>{
    console.log(response.data)
    setData(response.data);
  })
  },[])



  return (
    <div className="App">
      <h1>MERN TODO</h1>
      <input style={{height:50,width:500}} type='text' placeholder='Type the Task to be added in the list...'/><br/>
      <button>Show/Hide Tasks</button>
      <div>
        <h4>Tasks:</h4>
          <Show data={data}/>
      </div>
    </div>
  );
}

export default App;
