import React, { useState } from 'react'
import './App.css'
import Axios from 'axios'

function Show({data}) {
  const [update,setUpdate]=useState('');
  const [design,setDesign]=useState('none');
  const [clicked,setClicked]=useState(false);
//DELETE Operation:
const deletefn=(id)=>{
Axios.delete(`http://localhost:3001/delete/${id}`)
}

//UPDATE Operation:
const updatefn=(id)=>{
Axios.put('http://localhost:3001/update',{task:update,id:id});
}
const clickedfn=()=>{
  setClicked(!clicked);
  if(clicked){
  setDesign('line-through');
  }else{
    setDesign('none');
  }

}

  return (
    <div>{data.map((item)=>{
        return(<ol>
        <input type='checkbox' onClick={()=>clickedfn()}/><h4 style={{textDecoration:design}} className='task'>{(item.task)}</h4><button onClick={()=>deletefn(item._id)}>DELETE</button>
        <input type='text' placeholder='Input the change...' onChange={(e)=>setUpdate(e.target.value)}/><button onClick={()=>updatefn(item._id)}>UPDATE</button>
        </ol>)
    })}</div>
  )
}

export default Show
