import React from 'react'
import './App.css'
function Show({data}) {
  return (
    <div>{data.map((item)=>{
        return(<ol>
        <h4 className='task'>{(item.task)}</h4><button>DELETE</button><button>UPDATE</button>
        </ol>)
    })}</div>
  )
}

export default Show
